use hex::ToHex;

fn main() {
    // After building, crate::guest::IMAGE_ID is available (generated by embed_methods)
    let image_id_words: [u32; 8] = crate::guest::IMAGE_ID;
    // Convert to bytes big-endian
    let mut bytes = [0u8; 32];
    for (i, word) in image_id_words.iter().enumerate() {
        let off = i * 4;
        bytes[off..off + 4].copy_from_slice(&word.to_be_bytes());
    }
    let hex = bytes.encode_hex::<String>();
    println!("IMAGE_ID=0x{}", hex);
}


